def highest_product_string(numstring):
    total_high = 0
    products_list = []
    i = 0
    while i <=len(numstring) -13:
        group = numstring[i:i+13]
        product = 1
        for char in group:
            product *= int(char)
        
        if product > total_high:
            total_high = product
        products_list.append(product)
        i +=1
    print(total_high)
    print(max(products_list))
    return products_list

